import React from  'react';
class PerformanceForm extends React.Component{

    constructor(props) {
        super(props);
        this.state = {date: '', time:'', place:'', noOfSeats:'', artist:''};
        // this.handleChange = this.handleChange.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleDateChange=(event) =>{
        this.setState({date: event.target.value});
    }

    handleTimeChange=(event) =>{
        this.setState({time: event.target.value});
    }

    handlePlaceChange=(event) =>{
        this.setState({place: event.target.value});
    }

    handleSeatsChange=(event) =>{
        this.setState({noOfSeats: event.target.value});
    }

    handleArtistChange=(event) =>{
        this.setState({artist: event.target.value});
    }

    handleSubmit=(event) =>{
        const performance = {
            date: this.state.date,
            time: this.state.time,
            place: this.state.place,
            noOfSeats: this.state.noOfSeats,
            artist: this.state.artist
        };
        console.log('A performance was submitted: ');
        console.log(performance);
        this.props.addFunc(performance);
        event.preventDefault();
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Date:
                    <input type="date" value={this.state.date} onChange={this.handleDateChange} />
                </label><br/>
                <label>
                    Time:
                    <input type="datetime-local" value={this.state.time} onChange={this.handleDateChange} />
                </label><br/>
                <label>
                    Place:
                    <input type="text" value={this.state.place} onChange={this.handlePlaceChange} />
                </label><br/>
                <label>
                    Number of seats:
                    <input type="number" value={this.state.noOfSeats} onChange={this.handleSeatsChange} />
                </label><br/>
                <label>
                    Artist:
                    <input type="text" value={this.state.artist} onChange={this.handleArtistChange} />
                </label><br/>
                <input type="submit" value="Create performance" />
            </form>
        );
    }
}
export default PerformanceForm;
