import React from  'react';
import './PerformanceApp.css'

class PerformanceRow extends React.Component{

    handleClick=(event)=>{
        console.log('Delete button for: ' + this.props.performance.id);
        this.props.deleteFunc(this.props.performance.id);
    }

    render() {
        return (
            <tr>
                <td>{this.props.performance.id}</td>
                <td>{this.props.performance.date}</td>
                <td>{this.props.performance.place}</td>
                {/*<td>{this.props.performance.noOfSeats}</td>*/}
                <td>{this.props.performance.artist}</td>
                <td><button onClick={this.handleClick}>Delete performance</button></td>
            </tr>
        );
    }
}

class PerformanceTable extends React.Component {
    render() {
        const rows = [];
        const funcDel = this.props.deleteFunc;
        this.props.performances.forEach(function(performance) {
            rows.push(<PerformanceRow performance={performance} key={performance.id} deleteFunc={funcDel} />);
        });
        return (<div className="PerformanceTable">

                <table className="center">
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Date</th>
                        <th>Place</th>
                        {/*<th>Number of seats</th>*/}
                        <th>Artist</th>
                        <th></th>
                    </tr>
                    </thead>
                    <tbody>{rows}</tbody>
                </table>

            </div>
        );
    }
}

export default PerformanceTable;
